    def saveFile(byte[] bytes, String filePath) throws IOException {
        def tempFile = File.createTempFile(filePath,"png")
        final FileOutputStream fos = new FileOutputStream( tempFile);
        fos.write(bytes);
        fos.flush();
        fos.close();
        return tempFile;
    }
    def getThumbNail(def multipartFile, def width){
        BufferedImage image = ImageIO.read( multipartFile );
        int imageWidth = image.getWidth();
        int imageHeight = image.getHeight();
        //float factor = width/imageWidth;
        //System.out.println(imageWidth);
        //System.out.println(factor);
        //System.out.println(Float.valueOf(imageHeight*factor));
        //Image scaledImage = image.getScaledInstance((imageWidth*factor).intValue(),
        //										(imageHeight*factor).intValue(),
        //									java.awt.Image.SCALE_SMOOTH);
        Image scaledImage = Scalr.resize(image, width);
		
        return scaledImage;
		
    }
       
    def downloadToLocal(def remoteUrl, def fileName, def extension) {
        
        def fileExtension = extension
        if (fileExtension == null) {
            fileExtension = ".jpg"
        }
        
        BufferedImage imageBuffer = ImageIO.read(new URL(remoteUrl))
        
        def finalImageName = fileName + fileExtension     
        File outputFile = new File (grailsApplication.config.pictureLocation + File.separator + finalImageName)
        
        ImageIO.write(imageBuffer, fileExtension.substring(1), outputFile)
        
        return outputFile
        
    }
        
